import {ADD_ITEM,DELETE_ITEM,EDIT_ITEM,UPDATE_ITEM} from '../actions/marketActions';

//creating initial state 
const initialState = {
    item:null,
    location:"",
    name:"",
    price:"",
    error:(""),
    isFetching:false

};

const reducer = (state, action )=>{
    var list = JSON.parse(localStorage.getItem(''))

    switch (action.type){
        case "ADD_ITEM":
            return {
                ...state,
                error:(""),
                isFetching:true
            }
            case FETCH_DATA_SUCCESS:
                return {
                    ...state, 
                    item:action.payload.name,
                    location:action.payload.location,
                    name:action.payload.name,
                    price:action.payload.price,
                    error:(""),
                    isFetchibg:false,

                }
                case FETCH_DATA_FAILURE:
                    return {
                        ...state,
                        error:action.payload,
                        isFetching: false
                    }
                    default:
                        return state;
    }
 
}

export default reducer;
