import React, { Component } from 'react';
import MarketForm from "../components/MarketForm";
import {connect} from 'react-redux';
import * as actions from "../actions/marketActions";
import {bindActionCreators} from 'redux'

class MarketList extends Component {
    

    returnList () {
      if (localStorage.getItem('items') ==null)
      localStorage.setItem('items',JSON.stringify([]))
     return JSON.parse(localStorage.getItem('items'))
      
     }

    onAddOrEdit=(data)=> {
        var list = this.returnList()
        if (this.state.currentIndex === -1)
        list.push(data)
        else 
        list[this.state.currentIndex] = data
        localStorage.setItem('items',JSON.stringify(list))
        this.setState({list,currentIndex:-1})
    }

    handleEdit =index =>{
        this.props.updateIndex(index)
    }

    handleDelete =index =>{
        this.props.deleteItem(index)
    }
    render() {
        return (
            <div>
                <MarketForm/>
                     
                <br/>
                <h1>Market List</h1>
                <table>
                    <tbody>
                        {
                            this.props.list.map((item,index)=>{
                                return <tr key ={index}>
                                    <td>{item.location}</td>
                                    <td>{item.name}</td>
                                    <td>{item.price}</td>
                                    <td>{item.description}</td>
                                    <button onClick ={()=>this.handleEdit(index)}>Edit</button>
                                    <button onClick ={()=>this.handleDelete(index)}>Delete</button>
                                </tr>
                            })
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        list:state.list
    }
}

 const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        deleteItem : actions.DELETE_ITEM ,
        updateIndex:actions.UPDATE_INDEX
    },dispatch)
}

export default connect(mapStateToProps,mapDispatchToProps)(MarketList);
